mapper.update:
UPDATE DEPTBOARD
SET STATUS='N'
WHERE DBOARD_NO=#{dboardNo}

UPDATE DEPTBOARD
SET DBOARD_CATEGORY=#{dboardCategory},
	DBOARD_CONTENT=#{dboardContent},
	DBOARD_TITLE=#{dboardTitle}
WHERE DBOARD_NO=#{dboardNo}

UPDATE DEPTBOARD
SET COUNT=COUNT+1
WHERE DBOARD_NO=#{dboardNo}

mapper.select:
SELECT *
FROM DEPTBOARD
WHERE STATUS='Y'
AND (
DBOARD_TITLE LIKE #{searchKey}
or DBOARD_CONTENT LIKE #{searchKey}
)
