mapper.update:
UPDATE ROOM
SET ROOM_NAME =#{roomName},
	LIMIT_NUM =#{limitCount},
	LOCATION =#{location},
	STATUS =#{status}
WHERE ROOM_NO = #{roomNo}

mapper.select:
SELECT STA_TIME, END_TIME
FROM ROOM_RES
WHERE STATUS = 'Y'
AND RES_DATE = #{resDate}
AND ROOM_NO = #{roomNo}

mapper.update:

UPDATE ROOM_RES
SET STATUS = 'N'
WHERE RES_DATE < #{time}
