mapper.update:
UPDATE NOTICE
SET STATUS='N'
WHERE NOTICE_NO=#{noticeNo}

UPDATE NOTICE
SET NOTICE_CONTENT=#{noticeContent},
	NOTICE_TITLE=#{noticeTitle},
	ORIGIN_NAME=#{originName},
	CHANGE_NAME=#{changeName}
WHERE NOTICE_NO=#{noticeNo}

UPDATE NOTICE
SET COUNT=COUNT+1
WHERE NOTICE_NO=#{noticeNo}

mapper.select:
SELECT *
FROM NOTICE
WHERE STATUS='Y'
AND (
NOTICE_TITLE LIKE #{searchKey}
or NOTICE_CONTENT LIKE #{searchKey}
)
